<?phpuse soft\helpers\BootstrapHelper;use yii\helpers\ArrayHelper;/** * @throws \yii\web\NotFoundHttpException */function not_found($message = null){    if ($message == null) {        $message = t('Page not found.', 'yii');    }    throw new \yii\web\NotFoundHttpException($message);}/** * @throws \yii\web\ForbiddenHttpException */function forbidden($message = null){    if ($message == null) {        $message = t('You are not allowed to perform this action!', 'yii');    }    throw new \yii\web\ForbiddenHttpException($message);}/** * Checks bs version * @param $bsVersion string|int * @return bool * @throws \Exception */function isBs(int $bsVersion){    return BootstrapHelper::isBs($bsVersion);}/** * Check if bs version is 4 * @throws \Exception */function isBs4(){    return BootstrapHelper::isBs4();}/** * Check if bs version is 3 * @throws \Exception */function isBs3(){    return BootstrapHelper::isBs3();}