<?phpnamespace soft\helpers;use yii\helpers\Url as YiiUrl;class Url extends YiiUrl{    /**     * @param $params mixed     * @return string     */    public static function homeUrl($params = null)    {        return static::to('/', true);    }    public static function isHomePage()    {        return \Yii::$app->controller->route == 'site/index';    }}?>